mapper.update:
UPDATE merchants
SET enable = 1
WHERE merchant_id = #{merchantId}

UPDATE merchants
SET enable = 0
WHERE merchant_id = #{merchantId}

mapper.select:
SELECT distinct c.merchant_code
FROM wozhi_route a
    INNER JOIN wozhi_upstream b on a.upstream_id = b.upstream_id
    INNER JOIN wozhi_merchant c on a.merchant_id = c.merchant_id
WHERE b.upstream_alias = #{upstreamAlias}
    and a.third_merchant_code = #{thirdMerchantCode}

SELECT distinct c.merchant_id
FROM wozhi_route a
    INNER JOIN wozhi_upstream b on a.upstream_id = b.upstream_id
    INNER JOIN wozhi_merchant c on a.merchant_id = c.merchant_id
WHERE b.upstream_alias LIKE #{upstreamAlias}
    and a.third_merchant_code = #{thirdMerchantCode}
